import React, { useState } from "react";
import * as XLSX from "xlsx";
import axios from "axios";
import { Card, CardContent } from "../ui/card";
import { Button } from "ui/button";

const RedundancyChecker = () => {
  const [data, setData] = useState([]);
  const [redundancies, setRedundancies] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState("");

  const handleFileUpload = (event) => {
    const file = event.target.files[0];
    const reader = new FileReader();

    reader.onload = (e) => {
      const binaryStr = e.target.result;
      const workbook = XLSX.read(binaryStr, { type: "binary" });
      const sheetName = workbook.SheetNames[0];
      const sheet = XLSX.utils.sheet_to_json(workbook.Sheets[sheetName]);

      const formattedData = sheet.map((row) => Object.values(row)[0]);
      setData(formattedData);
    };

    reader.readAsBinaryString(file);
  };

  const checkRedundancy = async () => {
    if (data.length === 0) {
      setError("Please upload an Excel file first.");
      return;
    }

    setLoading(true);
    setError("");

    const formData = new FormData();
    const file = new Blob(
      [XLSX.write(XLSX.utils.book_new(), { bookType: "xlsx", type: "binary" })],
      { type: "application/vnd.openxmlformats-officedocument.spreadsheetml.sheet" }
    );
    formData.append("file", file, "data.xlsx");

    try {
      const response = await axios.post("http://127.0.0.1:5000/check_redundancy", formData, {
        headers: { "Content-Type": "multipart/form-data" },
      });
      setRedundancies(response.data.redundancies);
    } catch (err) {
      setError("Error processing file. Ensure backend is running.");
    }

    setLoading(false);
  };

  return (
    <div className="p-6 bg-gray-100 min-h-screen">
      <h1 className="text-2xl font-bold mb-4 text-center">Curriculum Redundancy Checker</h1>
      <div className="flex flex-col items-center space-y-4">
        <input type="file" accept=".xlsx, .xls" onChange={handleFileUpload} className="mb-4" />
        <Button onClick={checkRedundancy} disabled={loading}>
          {loading ? "Checking..." : "Check Redundancy"}
        </Button>
        {error && <p className="text-red-500">{error}</p>}
      </div>

      <div className="mt-6">
        <h2 className="text-lg font-semibold mb-2">Uploaded Subjects</h2>
        <div className="grid grid-cols-2 gap-4">
          {data.map((subject, index) => (
            <Card key={index}>
              <CardContent className="p-2">{subject}</CardContent>
            </Card>
          ))}
        </div>
      </div>

      <div className="mt-6">
        <h2 className="text-lg font-semibold mb-2">Redundant Course Pairs</h2>
        {redundancies.length > 0 ? (
          <ul className="bg-white p-4 rounded-lg shadow">
            {redundancies.map((item, index) => (
              <li key={index} className="p-2 border-b">
                <strong>{item.pair[0]}</strong> ↔ <strong>{item.pair[1]}</strong>  
                <span className="text-blue-600"> ({item.similarity})</span>
              </li>
            ))}
          </ul>
        ) : (
          <p>No redundancies detected.</p>
        )}
      </div>
    </div>
  );
};

export default RedundancyChecker;
