import React, { useState } from "react";
import * as XLSX from "xlsx";

const App = () => {
  const [file, setFile] = useState(null);
  const [tableData, setTableData] = useState([]);
  const [redundancies, setRedundancies] = useState([]);

  const handleFileUpload = (event) => {
    const uploadedFile = event.target.files[0];
    if (!uploadedFile.name.endsWith(".xlsx")) {
      alert("Only .xlsx files are supported.");
      return;
    }
    setFile(uploadedFile);
    readExcel(uploadedFile);
  };

  const readExcel = (file) => {
    const reader = new FileReader();
    reader.readAsBinaryString(file);
    reader.onload = (event) => {
      const binaryString = event.target.result;
      const workbook = XLSX.read(binaryString, { type: "binary" });

      // Get the first sheet
      const sheetName = workbook.SheetNames[0];
      const sheet = workbook.Sheets[sheetName];

      // Convert to JSON
      const data = XLSX.utils.sheet_to_json(sheet, { header: 1 });
      setTableData(data);
    };
  };

  const checkRedundancy = async () => {
    if (!file) {
      alert("Please upload an Excel file first.");
      return;
    }

    const formData = new FormData();
    formData.append("file", file);

    try {
      const response = await fetch("http://127.0.0.1:5000/check_redundancy", {
        method: "POST",
        body: formData,
      });

      const result = await response.json();
      console.log(result);

      if (result.error) {
        alert("Error: " + result.error);
      } else {
        setRedundancies(result.redundancies);
      }
    } catch (error) {
      console.error("Error uploading file:", error);
      alert("Failed to upload file.");
    }
  };

  return (
    <div className="flex flex-col items-center justify-center min-h-screen bg-gray-100 p-6">
      <div className="bg-white shadow-lg rounded-lg p-6 w-full max-w-4xl text-center">
        <h1 className="text-2xl font-bold text-gray-800 mb-4">
          üìö Curriculum Redundancy Checker
        </h1>

        {/* File Upload */}
        <div className="mb-4">
          <input
            type="file"
            onChange={handleFileUpload}
            className="block w-full text-sm text-gray-500 
                     file:mr-4 file:py-2 file:px-4 
                     file:rounded-lg file:border-0 
                     file:text-sm file:font-semibold 
                     file:bg-blue-600 file:text-white 
                     hover:file:bg-blue-700 cursor-pointer"
          />
        </div>

        {/* Check Redundancy Button */}
        <button
          onClick={checkRedundancy}
          className="bg-blue-600 text-white px-4 py-2 rounded-lg 
                     font-semibold shadow-md hover:bg-blue-700"
        >
          üîç Check Redundancy
        </button>

        {/* Uploaded Excel Table */}
        <div className="mt-6 w-full">
          <h2 className="text-xl font-semibold text-gray-700">
            üìÑ Uploaded Dataset
          </h2>
          {tableData.length > 0 ? (
            <div className="overflow-x-auto mt-4">
              <table className="w-full border-collapse border border-gray-300 bg-white shadow-lg">
                <thead>
                  <tr className="bg-gray-200">
                    {tableData[0].map((header, index) => (
                      <th key={index} className="border p-2 text-left">
                        {header}
                      </th>
                    ))}
                  </tr>
                </thead>
                <tbody>
                  {tableData.slice(1).map((row, rowIndex) => (
                    <tr key={rowIndex} className="border-t">
                      {row.map((cell, cellIndex) => (
                        <td key={cellIndex} className="border p-2">
                          {cell}
                        </td>
                      ))}
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          ) : (
            <p className="text-gray-500 mt-2">No data to display.</p>
          )}
        </div>

        {/* Redundant Course Pairs */}
        <div className="mt-6 w-full text-left">
          <h2 className="text-xl font-semibold text-gray-700">
            üîÅ Redundant Course Pairs
          </h2>
          {redundancies.length > 0 ? (
            <ul className="mt-3 space-y-2">
              {redundancies.map((item, index) => (
                <li
                  key={index}
                  className="bg-gray-200 p-3 rounded-lg shadow-md"
                >
                  <strong>{item.pair[0]}</strong> ‚Üî{" "}
                  <strong>{item.pair[1]}</strong> (
                  <span className="text-blue-600 font-semibold">
                    {item.similarity}
                  </span>
                  )
                </li>
              ))}
            </ul>
          ) : (
            <p className="text-gray-500 mt-2">No redundant subjects found.</p>
          )}
        </div>
      </div>
    </div>
  );
};

export default App;
