import React, { useState } from "react";
import * as XLSX from "xlsx";

const App = () => {
  const [data, setData] = useState([]);

  const handleFileUpload = (event) => {
    const file = event.target.files[0];
    if (!file) return;

    const reader = new FileReader();
    reader.readAsBinaryString(file);
    reader.onload = (e) => {
      const binaryString = e.target.result;
      const workbook = XLSX.read(binaryString, { type: "binary" });

      const sheetName = workbook.SheetNames[0];
      const sheet = XLSX.utils.sheet_to_json(workbook.Sheets[sheetName], { header: 1 });

      setData(sheet);
    };
  };

  return (
    <div className="min-h-screen bg-gray-100 flex flex-col items-center p-6">
      <h1 className="text-3xl font-bold text-gray-800 mb-6">
        üìö Curriculum Redundancy Checker
      </h1>

      {/* File Upload Buttons */}
      <div className="flex space-x-4 mb-6">
        <label className="px-5 py-3 bg-blue-600 text-white font-semibold rounded-lg shadow-md 
                          hover:bg-blue-700 hover:scale-105 transition-transform duration-200 cursor-pointer">
          üìÇ Choose File
          <input type="file" className="hidden" onChange={handleFileUpload} />
        </label>

        <button className="px-5 py-3 bg-green-600 text-white font-semibold rounded-lg shadow-md 
                           hover:bg-green-700 hover:scale-105 transition-transform duration-200">
          üîç Check Redundancy
        </button>
      </div>

      {/* Uploaded Data Table */}
      <div className="bg-white p-6 rounded-lg shadow-md w-full max-w-4xl">
        <h2 className="text-xl font-bold text-gray-700 mb-4">üìÑ Uploaded Dataset</h2>

        {data.length > 0 ? (
          <table className="w-full border-collapse border border-gray-300">
            <thead>
              <tr className="bg-gray-200">
                {data[0].map((header, index) => (
                  <th key={index} className="border border-gray-300 px-4 py-2 text-left font-semibold">
                    {header}
                  </th>
                ))}
              </tr>
            </thead>
            <tbody>
              {data.slice(1).map((row, rowIndex) => (
                <tr key={rowIndex} className="hover:bg-gray-100 transition">
                  {row.map((cell, cellIndex) => (
                    <td key={cellIndex} className="border border-gray-300 px-4 py-2">
                      {cell}
                    </td>
                  ))}
                </tr>
              ))}
            </tbody>
          </table>
        ) : (
          <p className="text-gray-500">No dataset uploaded yet.</p>
        )}
      </div>
    </div>
  );
};

export default App;
